import "../index.css";
import React, { useEffect } from "react";

import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] =
    React.useState(false);
  const [isCloseAllPopup, setIsCloseAllPopups] =
    React.useState(false);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  };
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  };
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  };
  function handleCloseAllPopups() {
    setIsAddPlacePopupOpen(false)
    se
  }
  return (
    <div className="page">
      <Header />
      <Main 
      onEditAvatar={handleEditAvatarClick}
      onEditProfile={handleEditProfileClick}
      onAddPlace={handleAddPlaceClick}

      />
      <Footer />
      <PopupWithForm
        name={"profile"}
        title={"Редактировать профиль"}
        buttonText={"Сохранить"}
        isOpen={isEditProfilePopupOpen}
        onClose={handleCloseAllPopups}
      >
        <>
          <input
            className="form__info form__info_user_name form__info_name-input"
            name="name"
            id="name-input"
            type="text"
            size="15"
            minlength="2"
            maxlength="40"
            required
            value="Жак-Ив Кусто"
          />
          <span className="form__input-error name-input-error"></span>
          <input
            className="form__info form__info_user_job form__info_job-input"
            name="profession"
            id="job-input"
            type="text"
            size="25"
            minlength="2"
            maxlength="200"
            required
            value="Исследователь океана"
          />
          <span className="form__input-error job-input-error"></span>
        </>
      </PopupWithForm>
      <PopupWithForm
      name={"place"}
      title={"Новое место"}
      buttonText={"Создать"}
      id={"place"}
      isOpen={isAddPlacePopupOpen}
      onClose={handleCloseAllPopups}>

        <>
        <input
                className="form__info form__info_place_name form__info_place-input"
                name="name"
                id="place-input"
                type="text"
                size="15"
                minlength="2"
                maxlength="30"
                required
                placeholder="Название"
                value=""
              />
              <span className="form__input-error place-input-error"></span>
              <input
                className="form__info form__info_place_link form__info_email-input"
                name="link"
                id="email-input"
                type="url"
                size="25"
                required
                placeholder="Сcылка на картинку"
                value=""
              />
              <span className="form__input-error email-input-error"></span>
        </>
      </PopupWithForm>
      <PopupWithForm
      name={"basket"}
      title={"Новое место"}
      buttonText={"Вы уверены?"}
      id={"delete"}>
      </PopupWithForm>
      <PopupWithForm
      name={"update-avatar"}
      title={"Обновить аватар"}
      buttonText={"Сохранить"}
      id={"update-avatar"}
      isOpen={isEditAvatarPopupOpen}
      onClose={handleCloseAllPopups}>
        <input
                className="form__info update-avatar__input form__info_avatar_link form__info_avatar-input"
                name="avatar-link"
                id="avatar-input"
                type="url"
                size="25"
                required
                placeholder=""
                value="https://somewebsite.com/someimage.jpg"
              />
              <span className="form__input-error avatar-input-error"></span>
      </PopupWithForm>






      {/* <div className="popup basket-popup" id="delete">
        <div className="form popup__container delete-card">
          <button
            className="popup__close form__close button-hover"
            type="button"
          ></button>
          <h2 className="delete-card__title">Вы уверены?</h2>
          <button
            className="form__save form__submit delete-card__submit"
            type="submit"
          >
            Да
          </button>
        </div>
      </div> */}

      {/* <div className="popup update-avatar" id="update-avatar">
        <div className="popup__container">
          <form
            className="form form-profile form_avatar"
            method="get"
            name="profile-info"
