import "../index.css";
import React, { useEffect } from "react";
import { Route, Routes, ProtectedRoute, Navigate, useNavigate } from 'react-router-dom';
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import Register from "./Register";
import Login from "./Login";
import PopupWithForm from "./PopupWithForm";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import ImagePopup from "./ImagePopup";
import InfoTooltip from "./InfoTooltip";
import api from "../utils/Api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import imageResult from "../images/Unionok2.svg";
import imageError from "../images/Union-2.svg";
import ProtectedRouteElement from "./ProtectedRoute";
import * as Auth from "../utils/Auth";
import { FOCUSABLE_SELECTOR } from "@testing-library/user-event/dist/utils";

function App() {
  //попап аватара
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  //попап профиля
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  //попап добавления карточки
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({
    name: "",
    link: "",
  });
  //информация о пользователе
  const [currentUser, setCurrentUser] = React.useState({});
  //информация о карточках
  const [cards, setCards] = React.useState([]);
  //положительный попап входа
  const [isInfoTooltipResultPopupOpen, setInfoTooltipResultPopupOpen] = React.useState(false);
  //попап ошибки входа
  const [isInfoTooltipErrorPopupOpen, setInfoTooltipErrorPopupOpen] = React.useState(false);
  //статус входа в систему пользователя
  const [isLoggedIn, setLoggedIn] = React.useState();

  const navigate = useNavigate();

  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([currentUser, cards]) => {
        setCurrentUser(currentUser);
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  function handleTooltipErrorClick() {
    setInfoTooltipErrorPopupOpen(true);
  }
  function handleTooltipResultClick() {
    setInfoTooltipResultPopupOpen(true);
  }
  function handleCardClick(card) {
    setSelectedCard(card);
  }
  function handleLogin() {
    setLoggedIn(true);

 } 
  function handleCloseAllPopups() {
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setInfoTooltipErrorPopupOpen(false);
    setInfoTooltipResultPopupOpen(false);
    setSelectedCard({ name: "", link: "" });
  }


  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    if (!isLiked) {
      api
        .setLike(card._id)
        .then((newCard) => {
          setCards((state) =>
            state.map((c) => (c._id === card._id ? newCard : c))
          );
        })
        .catch((err) => {
          console.error(err);
        });
    } else {
      api
        .unsetLike(card._id)
        .then((newCard) => {
          setCards((state) =>
            state.map((c) => (c._id === card._id ? newCard : c))
          );
        })
        .catch((err) => {
          console.error(err);
        });
    }
  }

  function handleCardDelete(card) {
    api
      .deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id && c));
        handleCloseAllPopups();
      })
      .catch((err) => {
        console.log(`${err}`);
      });
  }

  function handleUpdateUser(data) {
    api
      .updateUserInfo(data)
      .then((newUser) => {
        setCurrentUser(newUser);
        handleCloseAllPopups();
      })
      .catch((err) => {
        console.error(err);
      });
  }
  function handleUpdateAvatar(data) {
    api
      .changeAvatar(data)
      .then((newAvatar) => {
        setCurrentUser(newAvatar);
        handleCloseAllPopups();
      })
      .catch((err) => {
        console.error(err);
      });
  }

  function handleAddPlace(data) {
    api
      .addNewCard(data)
      .then((newCard) => {
        setCards([newCard, ...cards]);
        handleCloseAllPopups();
      })
      .catch((err) => {
        console.error(err);
      });
  }

  function handleRegisterUser(email, password) {
    Auth.register(email, password)
    .then((data) => {
      if(data) {
        handleTooltipResultClick();
        console.log("sucess");
        navigate("/sign-in", {replace: true});
      }
    })
    .catch((err) => {
      navigate("/sign-up");
      console.log("error");
      handleTooltipErrorClick();

      console.log(err);   
    })
  }



 function handleAuthorizeUser(email, password) {
  Auth.authorize(email, password)
  .then((data) => {
    if (data.jwt) {
      setFormValue({email: '', password: ''});
      setHeaderEmail(email);
      handleLogin();
      navigate('/');
  }
  })
  .catch((err) => {
    console.log(err);
    navigate("/sign-in")
    handleTooltipErrorClick()
});
 }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Routes>
         <Route path="/" element={
         <ProtectedRouteElement
           element={Main}
           isLoggedIn={isLoggedIn}
           onEditAvatar={handleEditAvatarClick}
           onEditProfile={handleEditProfileClick}
           onAddPlace={handleAddPlaceClick}
           onCardClick={handleCardClick}
           cards={cards}
           onCardLike={handleCardLike}
           onCardDelete={handleCardDelete}

           />} />  
         <Route 
         path="/sign-up"
         element={<Register onRegister={handleRegisterUser}/>}
        />
        <Route 
        path="/sign-in"
        element={<Login handleLogin={handleLogin} handleTooltipError={handleTooltipErrorClick}/>}
        />

        </ Routes >
        <Footer />

        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={handleCloseAllPopups}
          onUpdateUser={handleUpdateUser}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={handleCloseAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={handleCloseAllPopups}
          onAddPlace={handleAddPlace}
        />
        <PopupWithForm
          name={"basket"}
          title={"Новое место"}
          buttonText={"Вы уверены?"}
          id={"delete"}
          onClose={handleCloseAllPopups}
        />
        <ImagePopup
          isOpen={selectedCard}
          card={selectedCard}
          onClose={handleCloseAllPopups}
        />
        <InfoTooltip 
        title={"Вы успешно зарегистрировались!"}
        src={imageResult}
        isOpen={isInfoTooltipResultPopupOpen}
        onClose={handleCloseAllPopups}
        />
        <InfoTooltip 
        title={"Что-то пошло не так! Попробуйте еще раз!"}
        src={imageError}
        isOpen={isInfoTooltipErrorPopupOpen}
        onClose={handleCloseAllPopups}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}
export default App;
