import "../index.css";
import React, { useEffect } from "react";


import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import EditProfilePopup from "./EditProfilePopup";
import ImagePopup from "./ImagePopup";
import api from "../utils/Api";
import Card from "./Card.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import { CardsContext } from "../contexts/CardsContext";

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({name: '', link: ''});
  const [currentUser, setСurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([currentUser, cards]) => {
        setСurrentUser(currentUser);
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  function handleCardClick(card) {
    setSelectedCard(card);
  }
  function handleCloseAllPopups() {
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setSelectedCard({name: '', link: ''});
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    if (!isLiked) {
      api.setLike(card._id).then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      });
    } else {
        api.unsetLike(card._id).then((newCard) => {
          setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
          })}
  } 
  function handleCardDelete(card) {
    api
      .deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id && c));
        handleCloseAllPopups();
      })
      .catch((err) => {
        console.log(`${err}`);
      });
  } 

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CardsContext.Provider value={cards}>
    <div className="page">
      <Header />
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onCardClick={handleCardClick}
        cards={cards}
        onCardLike={handleCardLike}
        onCardDelete={handleCardDelete}
      />
      <Footer />

      <EditProfilePopup 
      isOpen={isEditProfilePopupOpen} 
      onClose={closeAllPopups} 
      />
      <PopupWithForm
        name={"place"}
        title={"Новое место"}
        buttonText={"Создать"}
        id={"place"}
        isOpen={isAddPlacePopupOpen}
        onClose={handleCloseAllPopups}
      >
        <>
          <input
            className="form__info form__info_place_name form__info_place-input"
            name="name"
            id="place-input"
            type="text"
            size="15"
            minLength="2"
            maxLength="30"
            required
            placeholder="Название"
          />
          <span className="form__input-error place-input-error" />
          <input
            className="form__info form__info_place_link form__info_email-input"
            name="link"
            id="email-input"
            type="url"
            size="25"
            required
            placeholder="Сcылка на картинку"
          />
          <span className="form__input-error email-input-error" />
        </>
      </PopupWithForm>
      <PopupWithForm
        name={"basket"}
        title={"Новое место"}
        buttonText={"Вы уверены?"}
        id={"delete"}
        onClose={handleCloseAllPopups}
      />
      <PopupWithForm
        name={"update-avatar"}
        title={"Обновить аватар"}
        buttonText={"Сохранить"}
        id={"update-avatar"}
        isOpen={isEditAvatarPopupOpen}
        onClose={handleCloseAllPopups}
      >
        <>
          <input
            className="form__info update-avatar__input form__info_avatar_link form__info_avatar-input"
            name="avatar-link"
            id="avatar-input"
            type="url"
            size="25"
            required
            placeholder=""
            defaultValue="https://somewebsite.com/someimage.jpg"
          />
          <span className="form__input-error avatar-input-error" />
        </>
      </PopupWithForm>
      <ImagePopup
        isOpen={selectedCard}
        card={selectedCard}
        onClose={handleCloseAllPopups}
      />
    </div>
    </CardsContext.Provider>
    </CurrentUserContext.Provider>
  );
}
export default App;
