  //     .then(([userInfo, cards]) => {
  //       setUserInfo({
  //         userName: userInfo.name,
  //         userDescription: userInfo.about,
  //         userAvatar: userInfo.avatar,
  //       });
  //       setCards(cards);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // }, []);

  const currentUser = React.useContext(CurrentUserContext);

  return (
    <main>
      <section className="profile">
        <div className="profile__info">
          <button className="profile__button" onClick={props.onEditAvatar}>
            <img
              className="profile__avatar"
              src={currentUser.userAvatar}
              alt="Фото профиля"
              style={{ backgroundImage: `url(${currentUser.userAvatar})` }}
            />
          </button>
          <h1 className="profile__title">{currentUser.userName}</h1>
          <button
            className="profile__edit-button button-hover"
            type="button"
            onClick={props.onEditProfile}
          />
          <p className="profile__subtitle">{currentUser.userDescription}</p>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={props.onAddPlace}
        />
      </section>
      <section className="elements">
        {cards.map((card) => (
          <Card
            key={card._id}
            card={card}
            link={card.link}
            name={card.name}
            likes={card.likes}
            onCardClick={props.onCardClick}
          />
        ))}
      </section>
    </main>
  );
}
export default Main;
