import React, { useState } from "react";
import Sign from "./Sign";
import {Link, useNavigate } from 'react-router-dom';
import * as Auth from '../utils/Auth';

function Register({onRegister}) {
    const [formValue, setFormValue] = useState({
        email: '',
        password: '',
      })
      const navigate = useNavigate();

    function handleChange(e) {
        const {name, value} = e.target;
        setFormValue({
            ...formValue,
            [name]: value
          });
    }
    function handleSubmit(e) {
          e.preventDefault();
          onRegister( formValue.email, formValue.password );

          // Auth.register(formValue.email, formValue.password)
          // .then((res) => {
          //   navigate('/sign-in', {replace: true});
            }


    return (
        <Sign
        title={"Регистрация"}
        buttonText={"Зарегистрироваться"}
        name={"register"}
        onChange={handleChange}
        onSubmitRegister={handleSubmitRegister}
        emailRegister={formValue.email}
        passwordRegister={formValue.password}
        >
        <>
        <h4 className="sign__subtitle">Уже зарегистрированы? <a href="/sign-in" className="sign__subtitle">Войти</a></h4>
        </>
        </Sign>
    )
}
export default Register;
