import React, {useEffect} from "react";
import api from "../utils/Api";

function Main(props) {

  const [userInfo, setUserInfo] = React.useState({})
useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
  .then(([userInfo, cards]) => {

    setUserInfo({
      userName: userInfo.name,
      about: userInfo.about,
      avatar: userInfo.avatar,
    });
    //cardsSection.renderItems(cards);
  })
  .catch((err) => {
    console.log(err);
  });
  });

    return (
        <main>
      <section className="profile">
        <div className="profile__info">
          <button className="profile__button"
          onClick={props.onEditAvatar}>
            <img
              className="profile__avatar"
              src={userInfo.avatar}
              alt="Фото профиля"
              //style={{ backgroundImage: `url(${userInfo.avatar})` }}
            />
          </button>
          <h1 className="profile__title">userName}</h1>
          <button
            className="profile__edit-button button-hover"
            type="button"
            onClick={props.onEditProfile}
          ></button>
          <p className="profile__subtitle">{userInfo.about}</p>
        </div>
        <button className="profile__add-button" type="button"
        onClick={props.onAddPlace}></button>
      </section>
      <section className="elements">
        <template id="card">
          <article className="card">
            <button className="card__delete button-hover" type="button"></button>
            <img
              className="card__image"
              src="https://pictures.s3.yandex.net/frontend-developer/cards-compressed/baikal.jpg"
              alt="Фото посещенного места"
            />
            <div className="card__name">
              <h2 className="card__title"></h2>
              <div className="card__likes">
                <button className="card__like" type="button"></button>
                <h4 className="card__count-likes"></h4>
              </div>
            </div>
          </article>
        </template>
      </section>
    </main>
    );
}
export default Main;
