import "../index.css";
import React from "react";

import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
//import ImagePopup from "./ImagePopup";

function App() {
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  //const [selectedCard, setSelectedCard] = React.useState(false);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }
  // function handleCardClick() {
  //   setSelectedCard(true);
  // }
  function handleCloseAllPopups() {
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    //setSelectedCard(false);
  }
  return (
    <div className="page">
      <Header />
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        //onCardClick={handleCardClick}
      />
      <Footer />
      {/* <PopupWithForm
        name={"profile"}
        title={"Редактировать профиль"}
        buttonText={"Сохранить"}
        isOpen={isEditProfilePopupOpen}
        onClose={handleCloseAllPopups}
      >
        <>
          <input
            className="form__info form__info_user_name form__info_name-input"
            name="name"
            id="name-input"
            type="text"
            size="15"
            minLength="2"
            maxLength="40"
            required
            value="Жак-Ив Кусто"
          />
          <span className="form__input-error name-input-error" />
          <input
            className="form__info form__info_user_job form__info_job-input"
            name="profession"
            id="job-input"
            type="text"
            size="25"
            minLength="2"
            maxLength="50"
            required
            value="Исследователь океана"
          />
          <span className="form__input-error job-input-error" />
        </>
      </PopupWithForm> */}
      <PopupWithForm
        name={"place"}
        title={"Новое место"}
        buttonText={"Создать"}
        id={"place"}
        isOpen={isAddPlacePopupOpen}
        onClose={handleCloseAllPopups}
      >
        <>
          <input
            className="form__info form__info_place_name form__info_place-input"
            name="name"
            id="place-input"
            type="text"
            size="15"
            minLength="2"
            maxLength="30"
            required
            placeholder="Название"
          />
          <span className="form__input-error place-input-error" />
          <input
            className="form__info form__info_place_link form__info_email-input"
            name="link"
            id="email-input"
            type="url"
            size="25"
            required
            placeholder="Сcылка на картинку"
          />
          <span className="form__input-error email-input-error" />
        </>
      </PopupWithForm>
      <PopupWithForm
        name={"basket"}
        title={"Новое место"}
        buttonText={"Вы уверены?"}
        id={"delete"}
        onClose={handleCloseAllPopups}
      />
      <PopupWithForm
        name={"update-avatar"}
        title={"Обновить аватар"}
        buttonText={"Сохранить"}
        id={"update-avatar"}
        isOpen={isEditAvatarPopupOpen}
        onClose={handleCloseAllPopups}
      >
        <>
          <input
            className="form__info update-avatar__input form__info_avatar_link form__info_avatar-input"
            name="avatar-link"
            id="avatar-input"
            type="url"
            size="25"
            required
            placeholder=""
            value="https://somewebsite.com/someimage.jpg"
          />
          <span className="form__input-error avatar-input-error" />
        </>
      </PopupWithForm>
      {/* <ImagePopup
        isOpen={selectedCard}
        card={selectedCard}
        onClose={handleCloseAllPopups}
      /> */}
    </div>
  );
}
export default App;
