import React, { useState } from "react";
import Sign from "./Sign";
import {useNavigate } from 'react-router-dom';
import * as Auth from '../utils/Auth';

function Login(props) {
    const [formValue, setFormValue] = useState({
        email: '',
        password: ''
      })
    const navigate = useNavigate();

    function handleChange(e) {
        const {name, value} = e.target;

        setFormValue({
          ...formValue,
          [name]: value
        });
      }
    function handleSubmit(e) {
        e.preventDefault();
        if (!formValue.email || !formValue.password){
          return;
        }
        Auth.authorize(formValue.email, formValue.password)
          .then((data) => {
            if (data.jwt) {
              setFormValue({email: '', password: ''});
              props.handleLogin();
              navigate('/', {replace: true});
              }
          })
          .catch((err) => {
            props.handleTooltipError();
            console.log(err)});

      }

  return (
    <Sign
      title={"Вход"}
      buttonText={"Войти"}
      name={"login"}
      onSubmitLogin={handleSubmit}
      onChangeLogin={handleChange}
      emailLogin={formValue.email}
      passwordLogin={formValue.password}
    >
      <>
        <h4 className="sign__subtitle">Еще не зарегистрированы? <a href="/sign-up" className="sign__subtitle">Регистрация</a></h4>
      </>
    </Sign>
  )
}
export default Login;
