import React from "react";
import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function EditAvatarPopup(props) {
    const [avatar, setAvatar] = React.useState('');
    const currentUser = React.useContext(CurrentUserContext);

    function handleAvatarChange(e) {
        setAvatar(e.target.value);
      }

    React.useEffect(() => {
        setAvatar(currentUser.avatar);
    }, [currentUser]);

    function handleSubmit(e) {
        e.preventDefault();
        props.onUpdateAvatar({
          avatar: avatar,
        });
      } 

    return(
        <PopupWithForm
        name={"update-avatar"}
        title={"Обновить аватар"}
        buttonText={"Сохранить"}
        id={"update-avatar"}
        isOpen={props.isOpen}
        onClose={props.onClose}
        onSubmit={handleSubmit}
      >
        <>
          <input
            className="form__info update-avatar__input form__info_avatar_link form__info_avatar-input"
            name="avatar-link"
            id="avatar-input"
            type="url"
            size="25"
            required
            placeholder=""
            //defaultValue="https://somewebsite.com/someimage.jpg"
            value={avatar || ''}
            onChange={handleAvatarChange.bing()}
          />
          <span className="form__input-error avatar-input-error" />
        </>
      </PopupWithForm>
    )
}
export default EditAvatarPopup;
