import React, { useEffect } from "react";
import api from "../utils/Api";
import Card from "./Card.js";

function Main(props) {
  const [userInfo, setUserInfo] = React.useState({});
  const [cards, setCards] = React.useState([]);
  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
      .then(([userInfo, cards]) => {
        setUserInfo({
          userName: userInfo.name,
          userDescription: userInfo.about,
          userAvatar: userInfo.avatar,
        });
        setCards(cards);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <main>
      <section className="profile">
        <div className="profile__info">
          <button className="profile__button" onClick={props.onEditAvatar}>
            <img
              className="profile__avatar"
              src={userInfo.userAvatar}
              alt="Фото профиля"
              style={{ backgroundImage: `url(${userInfo.userAvatar})` }}
            />
          </button>
          <h1 className="profile__title">{userInfo.userName}</h1>
          <button
            className="profile__edit-button button-hover"
            type="button"
            onClick={props.onEditProfile}
          />
          <p className="profile__subtitle">{userInfo.userDescription}</p>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={props.onAddPlace}
        />
      </section>
      <section className="elements">
        {cards.map((card, id) => (
          <Card
            key={id}
            card={card}
            link={card.link}
            name={card.name}
            likes={card.likes}
            onCardClick={props.onCardClick}
          />
        ))}
      </section>
    </main>
  );
}
export default Main;
