import React from "react";
function ImagePopup(props) {
  return (
    <div className={`popup image-popup ${props.card ? `popup_opened` : ""} `} id={`${props.id}`}>
      <article className="image-card">
        <button
          className="image-card__close popup__close button-hover"
          type="button" onClick={props.onClose}
        ></button>
        <img
          className="image-card__photo"
          src={props.card ? props.card.link : ''}
          alt={props.card ? props.card.name : ''}
        />
      <h2 className="image-card__title"> {props.card ? props.card.name : ''} </h2>
      </article>
    </div>
  )
}
export default ImagePopup;
